import React, { createElement } from 'react';
import PropTypes from 'prop-types';
import './arg_form.less';

// This is what is being generated by render() from the Arg class. It is called in FunctionForm
export const ArgForm = ({ argTypeInstance, templateProps, label, setLabel, expand, setExpand, onValueRemove }) => {
  // Do createElement stuff here.
  const templateElement = createElement(argTypeInstance.argType.template, Object.assign(templateProps, { setLabel }));

  return (
    <div className="canvas__arg">
      <div>
        <label className="clickable" onClick={() => setExpand(!expand)}>
          <i className={`fa fa-caret-${expand ? 'down' : 'right'}`}/> {label}
        </label>
        <div className="canvas__arg--controls" style={{ display: expand ? 'block' : 'none' }}>
          { templateElement }
        </div>
      </div>

      {expand && (
        <div className="canvas__arg--remove" onClick={onValueRemove}>
          <i className="fa fa-trash-o" />
        </div>
      )}
    </div>
  );
};

ArgForm.propTypes = {
  argTypeInstance: PropTypes.object,
  templateProps: PropTypes.object,
  label: PropTypes.string,
  setLabel: PropTypes.func,
  expand: PropTypes.bool,
  setExpand: PropTypes.func,
  onValueRemove: PropTypes.func,
};
