import React from 'react';
import PropTypes from 'prop-types';
import { Tooltip } from '../tooltip';

// This is what is being generated by render() from the Arg class. It is called in FunctionForm
export const ArgLabel = props => {
  const { className, label, help, expandable, expanded, setExpand, children } = props;
  const labelClassName = expandable ? 'canvas__arg--label clickable' : 'canvas__arg--label';

  return (
    <div className={`canvas__arg--header${className && ` ${className}`}`}>
      <Tooltip text={help} placement="left">
        {expandable ? (
          <label className={labelClassName} onClick={() => setExpand(!expanded)}>
            <i className={`fa fa-chevron-${expanded ? 'down' : 'right'}`} />
            {label}
          </label>
        ) : (
          <label className="canvas__arg--label">{label}</label>
        )}
      </Tooltip>
      {children}
    </div>
  );
};

ArgLabel.propTypes = {
  className: PropTypes.string,
  label: PropTypes.string.isRequired,
  help: PropTypes.string.isRequired,
  expandable: PropTypes.bool,
  expanded: PropTypes.bool,
  setExpand: PropTypes.func,
  children: PropTypes.oneOfType([PropTypes.node, PropTypes.element]).isRequired,
};
