import React from 'react';
import PropTypes from 'prop-types';
import { TooltipIcon } from '../tooltip_icon';

// This is what is being generated by render() from the Arg class. It is called in FunctionForm
export const ArgSimpleForm = ({ children, required, valueMissing, onRemove }) => {
  return (
    <div className="canvas__arg--simple">
      <div className="canvas__arg--simple-wrapper">
        <div className="canvas__arg--simple-top" />
        <div className="canvas__arg--simple-controls">{children}</div>
        <div className="canvas__arg--simple-bottom" />
      </div>
      {!required && (
        <div className="canvas__arg--remove">
          <i className="fa fa-trash-o clickable" onClick={onRemove} />
        </div>
      )}
      {valueMissing && (
        <div className="canvas__arg--required">
          <TooltipIcon
            placement="left"
            icon="error"
            text="This argument is required, you should specify a value."
          />
        </div>
      )}
    </div>
  );
};

ArgSimpleForm.propTypes = {
  children: PropTypes.node,
  required: PropTypes.bool,
  valueMissing: PropTypes.bool,
  onRemove: PropTypes.func.isRequired,
};
