import React from 'react';
import { Tooltip } from '../tooltip';

// This is what is being generated by render() from the Arg class. It is called in FunctionForm
export const SimpleFailure = () => {
  const description = 'The interface for this argument could not parse the value, so a fallback input is being used';
  return (
    <div className="canvas__arg--error canvas__arg--error-simple">
      <Tooltip text={description} placement="left">
        <i className="fa fa-exclamation-circle " />
      </Tooltip>
    </div>
  );
};
